import React, { useState, useEffect } from 'react'
import { useQuery, useLazyQuery } from '@apollo/client'
import { GET_USER, ALL_BOOKS } from './../queries'

const Recommend = (props) => {
  const gotUser = useQuery(GET_USER)
  const gotBooks = useQuery(ALL_BOOKS)
  const [genre, setGenre] = useState(null)
  const [getBooks, result] = useLazyQuery(ALL_BOOKS)
  const [books, setBooks] = useState([])

  useEffect(() => {
    if (gotUser.data) {
      setGenre(gotUser.data.me.favoriteGenre)
      //getBooks({ variables: { genre: genre } })
    }
  }, [gotUser])

  //const whatGenre = (genre) => {
    
  //}

  useEffect(() => {
    if (result.data) {
      setBooks(result.data.allBooks)
      console.log(result.data.allBooks, 'result.data.allBooks in useEffect')
    }
  }, [result])

  if (result.loading)  {
    return <div>loading...</div>
  }

  if (!props.show) {
    return null
  }

  return (
    <div>
      <h2>Recommendations</h2>
      books in your favorite genre <b>{genre}</b>
      <br />
      <br />
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.filter(b => b.genres.includes(genre)).map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  )
}

export default Recommend
