import React, { useState, useEffect } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import LoginForm from './components/LoginForm'
import { useQuery, useApolloClient } from '@apollo/client'
import { ALL_AUTHORS, ALL_BOOKS } from './queries'

const Notify = ({ errorMessage }) => {
  if ( !errorMessage ) {
    return null
  }

  return (
    <div style={{color: 'red'}}>
      {errorMessage}
    </div>
  )
}

const App = () => {
  const [page, setPage] = useState('authors')
  const allAuthors = useQuery(ALL_AUTHORS)
  const allBooks = useQuery(ALL_BOOKS)
  const [authors, setAuthors] = useState([])
  const [books, setBooks] = useState([])
  const [token, setToken] = useState(null)
  const [errorMessage, setErrorMessage] = useState(null)
  const client = useApolloClient()

  console.log(allBooks.data, 'allBooks.data in App.js')
  console.log(allAuthors.data, 'allAuthors.data in App.js')

  useEffect(() => {
    const token = localStorage.getItem('phonenumbers-user-token')
    if ( token ) {
      setToken(token)
    }
  }, [])

  useEffect(() => {
    if (allAuthors.data) {
      console.log(allAuthors.data.allAuthors, 'allAuthors.data.allAuthors in App.js')
      setAuthors(allAuthors.data.allAuthors)
    }
  }, [allAuthors])

  useEffect(() => {
    if (allBooks.data) {
      console.log(allBooks.data.allAuthors, 'allBooks.data.allAuthors in App.js')
      setBooks(allBooks.data.allBooks)
    }
  }, [allBooks])

  //if (allAuthors.loading || allBooks.loading)  {
  //  return <div>loading...</div>
  //}

  const logout = () => {
    setToken(null)
    localStorage.clear()
    client.resetStore()
    setPage('login')
  }

  const notify = (message) => {
    setErrorMessage(message)
    setTimeout(() => {
      setErrorMessage(null)
    }, 5000)
  }

  if (!token) {
    return (
      <div>
        <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('login')}>login</button>
        </div>

        <Notify errorMessage={errorMessage} />

        <LoginForm
          setToken={setToken}
          setPage={setPage}
          setError={notify}
          show={page === 'login'}
        />

        <Authors token={token} 
          authors={authors}
          show={page === 'authors'}
        />

        <Books books={books} 
          show={page === 'books'}
        />

      </div>
    )
  }

  return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
        <button onClick={logout} >logout</button>
      </div>

      <Notify errorMessage={errorMessage} />

      <Authors token={token} 
        authors={authors}
        show={page === 'authors'}
      />

      <Books books={books}
        show={page === 'books'}
      />

      <NewBook
        token={token}
        show={page === 'add'}
      />

    </div>
  )
}

export default App
