import React, { useState, useEffect } from 'react'
import { useQuery, useLazyQuery } from '@apollo/client'
import { ALL_BOOKS, ALL_GENRES } from './../queries'

const Books = (props) => {
  const allBooks = useQuery(ALL_BOOKS)
  const allGenres = useQuery(ALL_GENRES)
  const [getBooks] = useLazyQuery(ALL_BOOKS)
  const [books, setBooks] = useState([])
  const [genres, setGenres] = useState([])

  useEffect(() => {
    if (allBooks.data) {
      setBooks(allBooks.data.allBooks)
    }
  }, [allBooks])

  useEffect(() => {
    if (allGenres.data) {
      setGenres(allGenres.data.allGenres)
    }
  }, [allGenres])

  const clickGenre = (genre) => {
    setBooks(getBooks({ variables: { genre: genre } }))
  }

  //if (allBooks.loading)  {
  //  return <div>loading...</div>
  //}

  if (!props.show) {
    return null
  }

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
        {genres.map(a =>
          <button onClick={() => clickGenre(a)}>{a}</button>
        )}
      </div>
    </div>
  )
}

export default Books
