import React, { useState, useEffect } from 'react'
import { useQuery, useLazyQuery } from '@apollo/client'
import { ALL_BOOKS, ALL_GENRES } from './../queries'

const Books = (props) => {
  const allBooks = useQuery(ALL_BOOKS)
  const allGenres = useQuery(ALL_GENRES)
  const [getBooks, result] = useLazyQuery(ALL_BOOKS)
  const [books, setBooks] = useState(null)
  const [genres, setGenres] = useState(null)

  useEffect(() => {
    if (allBooks.data) {
      setBooks(allBooks.data.allBooks)
    }
  }, [allBooks])

  useEffect(() => {
    if (allGenres.data) {
      setGenres(allGenres.data.allGenres)
    }
  }, [allGenres])

  const clickGenre = (genre, event) => {
    event.preventDefault()
    getBooks({ variables: { genre: genre } })
  }

  useEffect(() => {
    if (result.data) {
      setBooks(result.data.allBooks)
      console.log(result.data.allBooks, 'result.data.allBooks in useEffect')
    }
  }, [result])

  if (result.loading) {
    return <div>loading...</div>
  }

  if (!props.show || !genres) {
    return null
  }

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
        {genres.map(a =>
          <button key={a} onClick={() => clickGenre(a)}>{a}</button>
        )}
        <button onClick={() => clickGenre('')}>all genres</button>
      </div>
    </div>
  )
}

export default Books
