import React, { useState } from 'react'
import Authors from './components/Authors'
import Books from './components/Books'
import NewBook from './components/NewBook'
import { gql, useQuery } from '@apollo/client'

export const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      born
      bookCount
    }
  }
`

export const ALL_BOOKS = gql`
  query {
    allBooks { 
      title 
      published
      author {
        name
      }
    }
  }
`

const App = () => {
  const [page, setPage] = useState('authors')
  const allAuthors = useQuery(ALL_AUTHORS)
  const allBooks = useQuery(ALL_BOOKS)
  const [authors, setAuthors] = useState([])
  const [books, setBooks] = useState([])

  console.log(allBooks.data, 'result.data in App.js')

  //useEffect(() => {
  //  if (allAuthors.data) {
  //    console.log(allAuthors.data.allAuthors, 'allAuthors.data.allAuthors in App.js')
  //    setAuthors(allAuthors.data.allAuthors)
  //  }
 // }, [allAuthors])

  //useEffect(() => {
  //  if (allBooks.data) {
  //    console.log(allBooks.data.allAuthors, 'allBooks.data.allAuthors in App.js')
  //    setBooks(allBooks.data.allBooks)
  //  }
  //}, [allBooks])

  return (
    <div>
      <div>
        <button onClick={() => setPage('authors')}>authors</button>
        <button onClick={() => setPage('books')}>books</button>
        <button onClick={() => setPage('add')}>add book</button>
      </div>

      <Authors authors={allAuthors}
        show={page === 'authors'}
      />

      <Books books={allBooks}
        show={page === 'books'}
      />

      <NewBook
        show={page === 'add'}
      />

    </div>
  )
}

export default App
